// Input Processing and Validation
const startTime = Date.now();

try {
  const input = $input.first().json;
  const inputCompany = input.query || input.company || '';
  const focus = input.focus || 'complete';

  console.log(`[GSA_INPUT] Processing: ${inputCompany}`);

  if (!inputCompany || inputCompany.trim() === '') {
    throw new Error('Company name or URL is required');
  }

  // Clean and normalize input
  const cleanCompany = inputCompany.trim();

  return {
    originalInput: inputCompany,
    cleanCompany: cleanCompany,
    focus: focus,
    timestamp: new Date().toISOString(),
    processingStartTime: startTime
  };

} catch (error) {
  console.error('[GSA_INPUT] Error:', error.message);
  return {
    error: true,
    errorMessage: error.message,
    originalInput: inputCompany || 'Unknown',
    cleanCompany: '',
    focus: 'complete',
    timestamp: new Date().toISOString()
  };
}

// Google Search Integration
const input = $input.first().json;

if (input.error) {
  return input; // Pass through errors
}

try {
  const GOOGLE_API_KEY = 'AIzaSyB0rv0QDGWy7Gno284IybZVNB9BDJzSEPs';
  const SEARCH_ENGINE_ID = '16331af4b4dc043f5';

  const searchCompanyInfo = async (companyName) => {
    try {
      const searchQuery = encodeURIComponent(`${companyName} company about contact`);
      const searchUrl = `https://www.googleapis.com/customsearch/v1?key=${GOOGLE_API_KEY}&cx=${SEARCH_ENGINE_ID}&q=${searchQuery}&num=5`;

      console.log(`[GSA_SEARCH] Querying: ${searchQuery}`);

      const response = await fetch(searchUrl);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.error) {
        console.error('[GSA_SEARCH] API Error:', data.error);
        throw new Error(`Google API Error: ${data.error.message}`);
      }

      if (data.items && data.items.length > 0) {
        console.log(`[GSA_SEARCH] Found ${data.items.length} results`);
        const firstResult = data.items[0];
        return {
          title: firstResult.title,
          snippet: firstResult.snippet,
          link: firstResult.link,
          displayLink: firstResult.displayLink,
          searchSuccessful: true
        };
      }

      console.log('[GSA_SEARCH] No search results found');
      return { searchSuccessful: false };
    } catch (error) {
      console.error('[GSA_SEARCH] Error:', error.message);
      return { searchSuccessful: false, searchError: error.message };
    }
  };

  const searchResults = await searchCompanyInfo(input.cleanCompany);

  // Extract company info from search results
  let extractedCompany = input.cleanCompany;
  let companyDomain = null;
  let realBusinessInfo = null;

  if (searchResults.searchSuccessful) {
    extractedCompany = searchResults.title.split(' - ')[0].split(' | ')[0] || extractedCompany;
    companyDomain = searchResults.displayLink;
    realBusinessInfo = searchResults.snippet;
    console.log(`[GSA_SEARCH] Found company: ${extractedCompany} at ${companyDomain}`);
  }

  return {
    ...input,
    extractedCompany,
    companyDomain,
    realBusinessInfo,
    searchResults: searchResults.searchSuccessful ? searchResults : null,
    searchStatus: searchResults.searchSuccessful ? 'success' : 'failed'
  };

} catch (error) {
  console.error('[GSA_SEARCH] Unexpected error:', error.message);
  return {
    ...input,
    searchError: error.message,
    searchStatus: 'error'
  };
}

// Industry Classification
const input = $input.first().json;

if (input.error) {
  return input; // Pass through errors
}

try {
  const detectBusinessType = (companyName, searchInfo) => {
    const searchText = (searchInfo || '').toLowerCase();
    const name = companyName.toLowerCase();
    const types = [];

    const serviceKeywords = {
      'staffing': ['staffing', 'recruiting', 'employment', 'workforce', 'personnel', 'talent'],
      'medical': ['medical', 'health', 'healthcare', 'pharma', 'surgical', 'care', 'hospital'],
      'technology': ['tech', 'software', 'IT', 'cyber', 'computer', 'digital', 'ai', 'cloud'],
      'security': ['security', 'protection', 'guard', 'safety', 'surveillance'],
      'consulting': ['consulting', 'advisory', 'solutions', 'services', 'professional'],
      'manufacturing': ['manufacturing', 'factory', 'production', 'industrial', 'materials'],
      'retail': ['retail', 'store', 'shopping', 'consumer', 'sales', 'franchise'],
      'finance': ['financial', 'bank', 'investment', 'insurance', 'lending'],
      'construction': ['construction', 'building', 'engineering', 'contractor'],
      'logistics': ['logistics', 'shipping', 'transport', 'delivery', 'supply chain']
    };

    for (const [type, keywords] of Object.entries(serviceKeywords)) {
      if (keywords.some(keyword => name.includes(keyword) || searchText.includes(keyword))) {
        types.push(type);
      }
    }

    if (types.length === 0) {
      types.push('services');
    }

    return types;
  };

  const businessTypes = detectBusinessType(input.extractedCompany, input.realBusinessInfo);
  const primaryType = businessTypes[0] || 'services';

  console.log(`[GSA_INDUSTRY] Classified ${input.extractedCompany} as: ${primaryType}`);

  return {
    ...input,
    businessTypes,
    primaryType,
    industryClassified: true
  };

} catch (error) {
  console.error('[GSA_INDUSTRY] Error:', error.message);
  return {
    ...input,
    industryError: error.message,
    businessTypes: ['services'],
    primaryType: 'services',
    industryClassified: false
  };
}

