// Input Processing Node - Parse and validate company data
const startTime = Date.now();
console.log('[GSA_INPUT] Starting input processing');

try {
  const input = $input.first().json;
  console.log('[GSA_INPUT] Raw input:', JSON.stringify(input, null, 2));

  // Extract parameters from different possible locations
  let company = '';
  let focus = 'complete';

  if (input.body) {
    company = input.body.company || input.body.companyName || '';
    focus = input.body.focus || 'complete';
  } else {
    company = input.company || input.companyName || '';
    focus = input.focus || 'complete';
  }

  // Validate inputs
  if (!company || company.trim() === '') {
    throw new Error('Company name or URL is required');
  }

  const validFocusTypes = ['qualification', 'decision_makers', 'value_prop', 'complete'];
  if (!validFocusTypes.includes(focus)) {
    focus = 'complete';
  }

  const result = {
    company: company.trim(),
    focus: focus,
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - startTime
  };

  console.log('[GSA_INPUT] Processed input:', result);
  return result;

} catch (error) {
  console.error('[GSA_INPUT] Error:', error.message);
  return {
    error: true,
    errorMessage: error.message,
    company: '',
    focus: 'complete',
    timestamp: new Date().toISOString()
  };
}

// Company Extraction Node - Extract company name and website
const startTime = Date.now();
console.log('[GSA_EXTRACT] Starting company extraction');

try {
  const input = $input.first().json;

  if (input.error) {
    return input;
  }

  let companyName = input.company;
  let websiteUrl = '';

  console.log('[GSA_EXTRACT] Processing company:', companyName);

  // Handle URL input
  if (companyName.includes('http') || companyName.includes('www.') || companyName.includes('.com')) {
    try {
      // Clean and parse URL
      if (!companyName.startsWith('http')) {
        websiteUrl = companyName.startsWith('www.') ? `https://${companyName}` : `https://www.${companyName}`;
      } else {
        websiteUrl = companyName;
      }

      const url = new URL(websiteUrl);
      companyName = url.hostname.replace('www.', '').split('.')[0];
      companyName = companyName.charAt(0).toUpperCase() + companyName.slice(1);

      console.log('[GSA_EXTRACT] Extracted from URL - Company:', companyName, 'Website:', websiteUrl);
    } catch (e) {
      console.log('[GSA_EXTRACT] URL parsing failed, treating as company name');
      companyName = companyName.replace(/[^a-zA-Z0-9\s]/g, '').trim();
      websiteUrl = `https://www.${companyName.toLowerCase()}.com`;
    }
  } else {
    // Handle company name input
    websiteUrl = `https://www.${companyName.toLowerCase().replace(/\s+/g, '')}.com`;
  }

  const result = {
    ...input,
    extractedCompanyName: companyName,
    extractedWebsiteUrl: websiteUrl,
    extractionTime: Date.now() - startTime
  };

  console.log('[GSA_EXTRACT] Extraction complete:', result);
  return result;

} catch (error) {
  console.error('[GSA_EXTRACT] Error:', error.message);
  return {
    ...input,
    error: true,
    errorMessage: error.message,
    extractedCompanyName: input.company || 'Unknown',
    extractedWebsiteUrl: ''
  };
}

// Industry Analysis Node - Analyze company industry and business type
const startTime = Date.now();
console.log('[GSA_INDUSTRY] Starting industry analysis');

try {
  const input = $input.first().json;

  if (input.error) {
    return input;
  }

  const companyName = input.extractedCompanyName.toLowerCase();
  const websiteUrl = input.extractedWebsiteUrl.toLowerCase();

  console.log('[GSA_INDUSTRY] Analyzing:', companyName);

  // Industry pattern matching function
  function analyzeIndustry(name, url) {
    // Aluminum/Metal Manufacturing
    if (name.includes('aluminum') || name.includes('metal') || name.includes('steel') || name.includes('alloy')) {
      return {
        industry: 'Aluminum Manufacturing & Construction Products',
        naicsCode: '331318',
        businessSize: 'Established manufacturer',
        businessType: 'Manufacturing with design/fabrication services',
        markets: ['Commercial construction', 'Residential', 'Industrial'],
        category: 'manufacturing'
      };
    }

    // Technology/Software
    if (name.includes('tech') || name.includes('software') || name.includes('IT') || name.includes('digital') || name.includes('cyber')) {
      return {
        industry: 'Information Technology Services',
        naicsCode: '541511',
        businessSize: 'Professional services firm',
        businessType: 'Technology consulting and services',
        markets: ['Enterprise clients', 'Government', 'Commercial'],
        category: 'technology'
      };
    }

    // Consulting/Professional Services
    if (name.includes('consult') || name.includes('advisor') || name.includes('solution') || name.includes('strategic')) {
      return {
        industry: 'Professional Consulting Services',
        naicsCode: '541611',
        businessSize: 'Professional services firm',
        businessType: 'Consulting and advisory services',
        markets: ['Corporate clients', 'Government agencies', 'Non-profits'],
        category: 'consulting'
      };
    }

    // Construction/Engineering
    if (name.includes('construct') || name.includes('build') || name.includes('contractor') || name.includes('engineer')) {
      return {
        industry: 'Construction and Engineering Services',
        naicsCode: '236220',
        businessSize: 'Construction contractor',
        businessType: 'Commercial and residential construction',
        markets: ['Commercial development', 'Government projects', 'Residential'],
        category: 'construction'
      };
    }

    // Healthcare/Medical
    if (name.includes('health') || name.includes('medical') || name.includes('clinic') || name.includes('hospital')) {
      return {
        industry: 'Healthcare and Medical Services',
        naicsCode: '621111',
        businessSize: 'Healthcare provider',
        businessType: 'Medical services and healthcare',
        markets: ['Healthcare systems', 'Government health', 'Private practice'],
        category: 'healthcare'
      };
    }

    // Default for unknown industries
    return {
      industry: 'Professional Services',
      naicsCode: '541990',
      businessSize: 'Professional services firm',
      businessType: 'Specialized business services',
      markets: ['Commercial clients', 'Government', 'Private sector'],
      category: 'general'
    };
  }

  const industryAnalysis = analyzeIndustry(companyName, websiteUrl);

  const result = {
    ...input,
    industryAnalysis: industryAnalysis,
    analysisTime: Date.now() - startTime
  };

  console.log('[GSA_INDUSTRY] Analysis complete:', industryAnalysis.industry);
  return result;

} catch (error) {
  console.error('[GSA_INDUSTRY] Error:', error.message);
  return {
    ...input,
    error: true,
    errorMessage: error.message,
    industryAnalysis: {
      industry: 'Unknown Industry',
      category: 'unknown'
    }
  };
}

// GSA Schedule Matching Node - Match industry to appropriate GSA Schedules
const startTime = Date.now();
console.log('[GSA_SCHEDULE] Starting GSA schedule matching');

try {
  const input = $input.first().json;

  if (input.error) {
    return input;
  }

  const industry = input.industryAnalysis;
  console.log('[GSA_SCHEDULE] Matching schedules for:', industry.category);

  // GSA Schedule matching function
  function matchGSASchedules(industryData) {
    const category = industryData.category;

    switch (category) {
      case 'manufacturing':
        return {
          primaryMatch: 'GSA Schedule 56 - Buildings & Building Materials',
          schedules: [
            { name: 'GSA Schedule 56', description: 'Buildings & Building Materials', priority: 'Primary' },
            { name: 'GSA Schedule 03FAC', description: 'Facilities Maintenance and Management', priority: 'Secondary' },
            { name: 'Special Item Numbers', description: 'Architectural metalwork, railings, fencing', priority: 'Tertiary' }
          ],
          competitiveAdvantage: 'Direct access to federal building renovation projects'
        };

      case 'technology':
        return {
          primaryMatch: 'GSA Schedule 70 - Information Technology',
          schedules: [
            { name: 'GSA Schedule 70', description: 'Information Technology', priority: 'Primary' },
            { name: 'GSA OASIS', description: 'Professional Services', priority: 'Secondary' },
            { name: 'CIO-SP3', description: 'IT Services for Federal Agencies', priority: 'Tertiary' }
          ],
          competitiveAdvantage: 'Access to $50B+ federal IT marketplace'
        };

      case 'consulting':
        return {
          primaryMatch: 'GSA OASIS - Professional Services',
          schedules: [
            { name: 'GSA OASIS', description: 'Professional Services', priority: 'Primary' },
            { name: 'GSA Schedule 541', description: 'Professional and Allied Services', priority: 'Secondary' },
            { name: 'GSA Schedule 874', description: 'Mission Oriented Business Integrated Services', priority: 'Tertiary' }
          ],
          competitiveAdvantage: 'Exclusive access to high-value consulting contracts'
        };

      case 'construction':
        return {
          primaryMatch: 'GSA Schedule 03FAC - Facilities Services',
          schedules: [
            { name: 'GSA Schedule 03FAC', description: 'Facilities Maintenance and Management', priority: 'Primary' },
            { name: 'GSA Schedule 56', description: 'Buildings & Building Materials', priority: 'Secondary' },
            { name: 'GSA MOBIS', description: 'Mission Oriented Business Integrated Services', priority: 'Tertiary' }
          ],
          competitiveAdvantage: 'Direct access to federal facility construction projects'
        };

      case 'healthcare':
        return {
          primaryMatch: 'GSA Schedule 621 - Professional Services',
          schedules: [
            { name: 'GSA Schedule 621', description: 'Professional and Allied Healthcare Services', priority: 'Primary' },
            { name: 'GSA OASIS', description: 'Professional Services', priority: 'Secondary' },
            { name: 'VA Schedules', description: 'Veterans Affairs Healthcare Contracts', priority: 'Tertiary' }
          ],
          competitiveAdvantage: 'Access to federal healthcare and VA contracts'
        };

      default:
        return {
          primaryMatch: 'GSA Schedule 541 - Professional Services',
          schedules: [
            { name: 'GSA Schedule 541', description: 'Professional and Allied Services', priority: 'Primary' },
            { name: 'GSA OASIS', description: 'Professional Services', priority: 'Secondary' }
          ],
          competitiveAdvantage: 'Access to diverse federal service opportunities'
        };
    }
  }

  const gsaScheduleMatch = matchGSASchedules(industry);

  const result = {
    ...input,
    gsaScheduleMatch: gsaScheduleMatch,
    scheduleMatchTime: Date.now() - startTime
  };

  console.log('[GSA_SCHEDULE] Matched primary schedule:', gsaScheduleMatch.primaryMatch);
  return result;

} catch (error) {
  console.error('[GSA_SCHEDULE] Error:', error.message);
  return {
    ...input,
    error: true,
    errorMessage: error.message,
    gsaScheduleMatch: {
      primaryMatch: 'GSA Schedule 541 - Professional Services',
      schedules: []
    }
  };
}

