console.log('[GSA_FORMAT] Starting response formatting');

try {
  const input = $input.first().json;

  if (input.error) {
    const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });
    return {
      response: `‚ö†Ô∏è **GSA Research Error**\n\nError analyzing "${input.company}": ${input.errorMessage}\n\n**Time**: ${currentTime}\n\n**Next Steps**: Try with company website URL or contact for manual research`,
      error: true,
      timestamp: new Date().toISOString()
    };
  }

  const companyName = input.extractedCompanyName;
  const website = input.extractedWebsiteUrl;
  const industry = input.industryAnalysis;
  const gsaSchedules = input.gsaScheduleMatch;
  const contacts = input.decisionMakers;
  const valueProp = input.valueProposition;
  const focus = input.focus;

  console.log('[GSA_FORMAT] Formatting response for focus:', focus);

  let response = '';

  // Business Profile Section (always included)
  if (focus === 'qualification' || focus === 'complete') {
    response += `üè¢ **${companyName} - GSA Qualification Assessment**\n\n`;
    response += `üìä **Business Profile:**\n`;
    response += `‚Ä¢ Industry: ${industry.industry}\n`;
    response += `‚Ä¢ NAICS Code: ${industry.naicsCode}\n`;
    response += `‚Ä¢ Business Type: ${industry.businessType}\n`;
    response += `‚Ä¢ Estimated Size: ${industry.businessSize}\n`;
    response += `‚Ä¢ Website: ${website}\n\n`;

    response += `üéØ **GSA Schedule Opportunities:**\n`;
    gsaSchedules.schedules.forEach(schedule => {
      response += `‚Ä¢ **${schedule.name}** - ${schedule.description} (${schedule.priority})\n`;
    });
    response += `\n**Primary Match**: ${gsaSchedules.primaryMatch}\n`;
    response += `**Competitive Advantage**: ${gsaSchedules.competitiveAdvantage}\n\n`;
  }

  // Decision Makers Section
  if (focus === 'decision_makers' || focus === 'complete') {
    response += `üë• **Likely Decision Makers:**\n`;
    contacts.forEach(dm => {
      response += `‚Ä¢ **${dm.name}** - ${dm.title}\n`;
      response += `  üìß ${dm.email} | üìû ${dm.phone}\n`;
      response += `  Authority: ${dm.authority} | Priority: ${dm.priority}\n`;
      response += `  Notes: ${dm.notes}\n\n`;
    });
  }

  // Value Proposition Section
  if (focus === 'value_prop' || focus === 'complete') {
    response += `üí° **Industry-Specific Value Proposition:**\n\n`;

    response += `**Current Industry Challenges:**\n`;
    valueProp.challenges.forEach(challenge => {
      response += `‚Ä¢ ${challenge}\n`;
    });

    response += `\n**GSA Schedule Benefits for ${industry.industry}:**\n`;
    valueProp.benefits.forEach(benefit => {
      response += `‚Ä¢ ${benefit}\n`;
    });

    response += `\n**Specific Federal Opportunities:**\n`;
    valueProp.opportunities.forEach(opp => {
      response += `‚Ä¢ ${opp}\n`;
    });

    response += `\n**üéØ Call Strategy:**\n`;
    response += `**Opening Questions:**\n`;
    valueProp.questions.forEach(q => {
      response += `‚Ä¢ "${q}"\n`;
    });

    response += `\n**Closing Points:**\n`;
    valueProp.closingPoints.forEach(point => {
      response += `‚Ä¢ ${point}\n`;
    });
  }

  // Footer
  const totalTime = Date.now() - (input.timestamp ? new Date(input.timestamp).getTime() : startTime);
  response += `\n---\nüìä **Research Status**: Real business analysis completed\n`;
  response += `‚è±Ô∏è **Total Processing Time**: ${totalTime}ms\n`;
  response += `üéØ **Next Step**: Schedule consultation with GSA specialist\n`;
  response += `üí° **Note**: Contact details are estimated - verify during outreach`;

  const result = {
    response: response,
    metadata: {
      company: companyName,
      industry: industry.industry,
      primarySchedule: gsaSchedules.primaryMatch,
      contactCount: contacts.length,
      focus: focus,
      processingTime: totalTime,
      timestamp: new Date().toISOString()
    },
    formatTime: Date.now() - startTime
  };

  console.log('[GSA_FORMAT] Response formatted successfully, length:', response.length);
  return result;

} catch (error) {
  console.error('[GSA_FORMAT] Error:', error.message);
  const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

  return {
    response: `üö® **Response Formatting Error**\n\nError formatting GSA research response: ${error.message}\n\n**Time**: ${currentTime}\n**Contact**: System administrator`,
    error: true,
    timestamp: new Date().toISOString(),
    errorMessage: error.message
  };
}
