// Enhanced File Processing Input Validator
const startTime = Date.now();
console.log('[FILE_PROCESSOR] Starting enhanced file processing');

try {
  const input = $input.first().json;
  console.log('[FILE_PROCESSOR] Raw webhook data:', JSON.stringify(input, null, 2));

  // Extract file metadata from payload
  const fileMetadata = input.fileMetadata || {};
  const fileUrl = input.metadata?.attachmentUrl || input.fileUrl;
  const fileType = fileMetadata.fileType || 'UNKNOWN';
  const extractedText = fileMetadata.extractedText || '';
  const userPrompt = input.userPrompt || input.message || '';

  // Validate required fields
  if (!fileUrl) {
    throw new Error('File URL is required for processing');
  }

  // Determine processing strategy
  const processingNeeded = {
    ocrRequired: fileType === 'PDF' || (fileType === 'IMAGE' && !extractedText),
    businessAnalysis: true,
    workflowTriggers: true,
    externalDocCreation: extractedText.length > 5000 || fileType === 'PDF'
  };

  const result = {
    // Basic file info
    fileUrl: fileUrl,
    fileType: fileType,
    fileName: fileMetadata.fileName || fileUrl.split('/').pop(),
    metadataId: fileMetadata.metadataId,

    // Content analysis
    extractedText: extractedText,
    userPrompt: userPrompt,
    businessContext: fileMetadata.businessContext,

    // Processing flags
    processingNeeded: processingNeeded,

    // User context
    userId: input.userId || input.metadata?.userId,
    channelId: input.channelId || input.metadata?.channelId,
    userName: input.userName || 'File Processor',

    // Workflow metadata
    timestamp: new Date().toISOString(),
    workflowVersion: '2.0',
    processingStartTime: startTime,
    requestId: require('crypto').randomBytes(8).toString('hex')
  };

  console.log('[FILE_PROCESSOR] Input validated successfully:', {
    fileUrl: result.fileUrl,
    fileType: result.fileType,
    hasExtractedText: !!result.extractedText,
    processingNeeded: result.processingNeeded
  });

  return result;

} catch (error) {
  console.error('[FILE_PROCESSOR] Input validation error:', error.message);
  return {
    error: true,
    errorMessage: error.message,
    timestamp: new Date().toISOString(),
    processingStartTime: startTime
  };
}

