// Business Intelligence Processor
const startTime = Date.now();
console.log('[FILE_PROCESSOR] Processing AI analysis results');

try {
  const originalData = $('1. Input Validator').first().json;
  const aiResponse = $input.first().json;

  console.log('[FILE_PROCESSOR] AI Response:', JSON.stringify(aiResponse, null, 2));

  // Parse AI response (handle different response formats)
  let analysis = {};

  if (aiResponse.choices && aiResponse.choices[0]?.message?.content) {
    try {
      analysis = JSON.parse(aiResponse.choices[0].message.content);
    } catch (parseError) {
      console.warn('[FILE_PROCESSOR] Failed to parse AI response as JSON:', parseError.message);
      analysis = {
        description: aiResponse.choices[0].message.content,
        tags: [],
        categories: ['document'],
        entities: [],
        actionItems: []
      };
    }
  } else if (typeof aiResponse.output === 'string') {
    try {
      analysis = JSON.parse(aiResponse.output);
    } catch (parseError) {
      analysis = {
        description: aiResponse.output,
        tags: [],
        categories: ['document'],
        entities: [],
        actionItems: []
      };
    }
  } else {
    analysis = aiResponse;
  }

  // Enhance analysis with business intelligence
  const enhancedAnalysis = {
    // Original file data
    fileUrl: originalData.fileUrl,
    fileType: originalData.fileType,
    fileName: originalData.fileName,
    metadataId: originalData.metadataId,

    // AI Analysis Results
    extractedText: analysis.extractedText || originalData.extractedText || '',
    description: analysis.description || 'Business document processed',
    businessContext: analysis.businessContext || analysis.summary || '',
    aiSummary: analysis.summary || analysis.description || '',

    // Categorization
    tags: analysis.tags || [],
    categories: analysis.categories || ['document'],
    entities: analysis.entities || [],
    actionItems: analysis.actionItems || [],

    // Business Intelligence
    urgency: analysis.urgency || 'medium',
    businessValue: analysis.businessValue || 'medium',
    location: analysis.location || null,
    compliance: analysis.compliance || null,
    nextSteps: analysis.nextSteps || [],

    // Workflow Routing
    shouldCreateExternalDoc: (
      (analysis.extractedText && analysis.extractedText.length > 5000) ||
      originalData.fileType === 'PDF' ||
      analysis.businessValue === 'high'
    ),
    workflowTriggers: [
      ...(analysis.actionItems && analysis.actionItems.length > 0 ? ['task_management'] : []),
      ...(analysis.entities && analysis.entities.length > 3 ? ['crm_integration'] : []),
      ...(analysis.urgency === 'high' ? ['priority_notification'] : []),
      ...(analysis.compliance && analysis.compliance.regulatory ? ['compliance_review'] : []),
      'business_intelligence'
    ],

    // Processing metadata
    userId: originalData.userId,
    channelId: originalData.channelId,
    userName: originalData.userName,
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - originalData.processingStartTime,
    aiProcessingTime: Date.now() - startTime,
    requestId: originalData.requestId
  };

  console.log('[FILE_PROCESSOR] Enhanced analysis completed:', {
    fileType: enhancedAnalysis.fileType,
    hasExtractedText: !!enhancedAnalysis.extractedText,
    entityCount: enhancedAnalysis.entities.length,
    actionItemCount: enhancedAnalysis.actionItems.length,
    urgency: enhancedAnalysis.urgency,
    businessValue: enhancedAnalysis.businessValue,
    shouldCreateExternalDoc: enhancedAnalysis.shouldCreateExternalDoc,
    workflowTriggers: enhancedAnalysis.workflowTriggers
  });

  return enhancedAnalysis;

} catch (error) {
  console.error('[FILE_PROCESSOR] Analysis processing error:', error.message);
  const originalData = $('1. Input Validator').first().json;

  return {
    ...originalData,
    error: true,
    errorMessage: error.message,
    errorStep: 'ai_analysis_processing',
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - originalData.processingStartTime
  };
}

