// Format search results
const results = $input.all();
let formattedMessage = '';

if (!results || results.length === 0) {
  formattedMessage = 'No results found for your search query.';
} else {
  formattedMessage = `Found ${results.length} result${results.length > 1 ? 's' : ''}:\n\n`;

  results.forEach((item, index) => {
    const result = item.json;
    const location = result.channelName ? 
      `#${result.channelName} in ${result.serverName}` : 
      `DM with ${result.otherUserName || result.locationName}`;

    const preview = result.content ? 
      result.content.substring(0, 100) + (result.content.length > 100 ? '...' : '') : 
      '[File attachment]';

    const date = new Date(result.createdAt).toLocaleString();

    formattedMessage += `**${index + 1}. ${result.userName}** in ${location}\n`;
    formattedMessage += `   ${preview}\n`;
    formattedMessage += `   _${date}_\n\n`;
  });
}

return [{
  message: formattedMessage,
  timestamp: new Date().toISOString(),
  userId: 'assistant',
  type: 'search_results',
  metadata: {
    resultCount: results.length,
    searchTerm: $('Search Query Selector').item.json.searchTerm,
    intent: 'search',
    responseFound: true
  }
}];

// Handle errors gracefully
const error = $input.first().error;
const originalInput = $('Merge Inputs').first()?.json || {};

console.error('Workflow error:', error);

return [{
  message: 'I encountered an error processing your request. Please try again or contact support if the issue persists.',
  timestamp: new Date().toISOString(),
  userId: 'assistant',
  type: 'error_response',
  metadata: {
    error: error.message || 'Unknown error',
    originalMessage: originalInput.input || '',
    intent: originalInput.intent || 'unknown',
    platform: 'n8n-assistant',
    responseFound: false
  }
}];
