// Enhanced Input Processor Update for v5.1 - Dynamic Channel Creation
// This code handles external calls by creating dedicated channels per tool/workflow

const input = $input.first();
let normalizedData = {};

// System server configuration - your dedicated System server
const SYSTEM_SERVER_ID = 'a90f1d41-12a9-4586-b9a4-a513d3bd01d9'; // Your system server ID
const DEFAULT_SYSTEM_CHANNEL_ID = 'clwtz9i7m0008n9n01vhep8zt'; // Fallback system channel

// Channel naming function
function getChannelName(source, toolName, workflowName, isSystemMessage = false) {
  const prefix = 'sys-';

  if (isSystemMessage) return `${prefix}system-messages`;
  if (source === 'vapi') return `${prefix}vapi-calls`;
  if (source === 'external-api') return `${prefix}external-api`;
  if (source === 'n8n-test' || source === 'n8n-chat-test') return `${prefix}workflow-test`;
  if (source === 'discordant-folio') return `${prefix}folio-integration`;
  if (toolName) return `${prefix}tool-${toolName.toLowerCase().replace(/[^a-z0-9]/g, '-')}`;
  if (workflowName) return `${prefix}${workflowName.toLowerCase().replace(/[^a-z0-9]/g, '-')}`;

  return `${prefix}general`; // Fallback
}

// Detect if this is a system message
function isSystemMessage(content) {
  const systemPhrases = [
    'hi system',
    'system message',
    'testing workflow',
    'tool call',
    'workflow execution',
    'henceforth'
  ];

  return systemPhrases.some(phrase => 
    content.toLowerCase().includes(phrase.toLowerCase())
  );
}

try {
  if (input.json.chatInput) {
    // Test interface mode
    const testSessionId = require('crypto').randomBytes(16).toString('hex');
    const channelName = getChannelName('n8n-test');

    normalizedData = {
      input: input.json.chatInput || '',
      userId: 'test-user',
      channelName: channelName,
      channelId: null, // Will be set after channel creation
      sessionId: testSessionId,
      platform: 'n8n-chat-test',
      timestamp: new Date().toISOString(),
      isTestMode: true,
      isExternalCall: true,
      userName: 'Test User',
      serverId: SYSTEM_SERVER_ID,
      needsChannelCreation: true
    };
  } else {
    // Webhook mode
    let payload = {};

    // Parse payload from various sources
    if (input.json.body && typeof input.json.body === 'object') {
      payload = input.json.body;
    } else if (input.json.body && typeof input.json.body === 'string') {
      try {
        payload = JSON.parse(input.json.body);
      } catch (e) {
        payload = input.json || {};
      }
    } else {
      payload = input.json || {};
    }

    const messageContent = payload.message || payload.content || '';
    const isSystemMsg = isSystemMessage(messageContent);

    // Detect if this is an external/unknown call
    const isExternalCall = !payload.channelId || 
                          payload.channelId === 'unknown-channel' ||
                          payload.channelId === 'unknown' ||
                          payload.serverId === 'unknown-server' ||
                          payload.platform === 'external-api' ||
                          payload.platform === 'vapi' ||
                          isSystemMsg;

    // Determine channel name based on source and content
    let channelName;
    let needsChannelCreation = false;

    if (isExternalCall) {
      channelName = getChannelName(
        payload.platform,
        payload.toolName,
        payload.workflowName,
        isSystemMsg
      );
      needsChannelCreation = true;
    } else {
      // Use existing channel for internal calls
      channelName = null; // Will use existing channelId
    }

    // Build normalized data
    normalizedData = {
      input: messageContent,
      userId: payload.userId || 'external-user',
      channelName: channelName,
      channelId: isExternalCall ? null : payload.channelId, // Will be set after channel creation for external calls
      sessionId: payload.sessionId || `${payload.userId || 'external'}-${channelName || payload.channelId}`,
      platform: payload.platform || 'discordant',
      timestamp: payload.timestamp || new Date().toISOString(),
      isTestMode: false,
      isExternalCall: isExternalCall,
      isSystemMessage: isSystemMsg,
      needsChannelCreation: needsChannelCreation,
      userName: payload.userName || (isExternalCall ? 'External API User' : 'Unknown User'),
      serverId: isExternalCall ? SYSTEM_SERVER_ID : payload.serverId,
      // Tool/workflow metadata
      toolName: payload.toolName || null,
      workflowName: payload.workflowName || null,
      // Preserve original values for logging
      originalChannelId: payload.channelId,
      originalServerId: payload.serverId,
      externalSource: isExternalCall ? (payload.platform || 'api') : null,
      // Fallback channel for emergencies
      fallbackChannelId: DEFAULT_SYSTEM_CHANNEL_ID
    };

    console.log(`Processing ${isExternalCall ? 'EXTERNAL' : 'INTERNAL'} call`);
    console.log(`Channel strategy: ${needsChannelCreation ? `CREATE "${channelName}"` : `USE EXISTING "${payload.channelId}"`}`);
    console.log(`System message detected: ${isSystemMsg}`);
  }

  normalizedData.processingStartTime = Date.now();

  return normalizedData;

} catch (error) {
  console.error('Input processor error:', error.message);

  // Fallback to default system channel on error
  return {
    input: 'Error processing input: ' + error.message,
    userId: 'error-user',
    channelName: 'sys-error-handling',
    channelId: null,
    sessionId: 'error-session',
    platform: 'error',
    timestamp: new Date().toISOString(),
    isTestMode: false,
    isExternalCall: true,
    needsChannelCreation: true,
    errorOccurred: true,
    errorMessage: error.message,
    userName: 'Error Handler',
    serverId: SYSTEM_SERVER_ID,
    fallbackChannelId: DEFAULT_SYSTEM_CHANNEL_ID
  };
}

// Channel Manager Node for n8n
// Place this node right after the Enhanced Input Processor
// This node ensures the required channel exists in the System server

const inputData = $input.first().json;

// Discord API configuration
const DISCORD_BOT_TOKEN = $env.DISCORD_BOT_TOKEN; // Set this in n8n environment
const SYSTEM_SERVER_ID = 'a90f1d41-12a9-4586-b9a4-a513d3bd01d9';

async function createChannel(channelName, serverId = SYSTEM_SERVER_ID) {
  try {
    console.log(`Creating channel: ${channelName} in server: ${serverId}`);

    const response = await fetch(`https://discord.com/api/v10/guilds/${serverId}/channels`, {
      method: 'POST',
      headers: {
        'Authorization': `Bot ${DISCORD_BOT_TOKEN}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        name: channelName,
        type: 0, // Text channel
        topic: `Auto-created channel for ${channelName.replace('sys-', '')} operations`,
        permission_overwrites: [] // Inherit server permissions
      })
    });

    if (!response.ok) {
      const error = await response.text();
      console.error(`Discord API error:`, error);
      throw new Error(`Discord API error: ${response.status} - ${error}`);
    }

    const channel = await response.json();
    console.log(`Channel created successfully: ${channel.id}`);
    return channel;

  } catch (error) {
    console.error('Channel creation error:', error.message);
    throw error;
  }
}

async function findExistingChannel(channelName, serverId = SYSTEM_SERVER_ID) {
  try {
    const response = await fetch(`https://discord.com/api/v10/guilds/${serverId}/channels`, {
      headers: {
        'Authorization': `Bot ${DISCORD_BOT_TOKEN}`
      }
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch channels: ${response.status}`);
    }

    const channels = await response.json();
    const existingChannel = channels.find(ch => ch.name === channelName && ch.type === 0);

    if (existingChannel) {
      console.log(`Found existing channel: ${existingChannel.id}`);
      return existingChannel;
    }

    return null;
  } catch (error) {
    console.error('Channel lookup error:', error.message);
    return null;
  }
}

// Main channel management logic
async function manageChannel() {
  try {
    // If no channel creation needed, pass through
    if (!inputData.needsChannelCreation) {
      console.log('Using existing channel:', inputData.channelId);
      return {
        ...inputData,
        channelId: inputData.channelId,
        channelCreated: false,
        channelExists: true
      };
    }

    const channelName = inputData.channelName;
    console.log(`Managing channel: ${channelName}`);

    // First, try to find existing channel
    let channel = await findExistingChannel(channelName);
    let channelCreated = false;

    // If not found, create it
    if (!channel) {
      console.log(`Channel ${channelName} not found, creating...`);
      channel = await createChannel(channelName);
      channelCreated = true;
    }

    // Return updated data with channel ID
    return {
      ...inputData,
      channelId: channel.id,
      channelCreated: channelCreated,
      channelExists: true,
      channelData: {
        id: channel.id,
        name: channel.name,
        topic: channel.topic,
        type: channel.type
      }
    };

  } catch (error) {
    console.error('Channel management failed:', error.message);

    // Fallback to default system channel
    console.log('Falling back to default system channel');
    return {
      ...inputData,
      channelId: inputData.fallbackChannelId,
      channelCreated: false,
      channelExists: true,
      channelError: error.message,
      usingFallback: true
    };
  }
}

// Execute and return result
return manageChannel();

